import{_ as d,c as a,o as r,ag as e}from"./chunks/framework.DPDPlp3K.js";const u=JSON.parse('{"title":"指令帮助","description":"","frontmatter":{},"headers":[],"relativePath":"guide/commands.md","filePath":"guide/commands.md"}'),l={name:"guide/commands.md"};function s(h,t,o,i,n,b){return r(),a("div",null,t[0]||(t[0]=[e('<h1 id="指令帮助" tabindex="-1">指令帮助 <a class="header-anchor" href="#指令帮助" aria-label="Permalink to &quot;指令帮助&quot;">​</a></h1><h2 id="velocity跨服" tabindex="-1">Velocity跨服 <a class="header-anchor" href="#velocity跨服" aria-label="Permalink to &quot;Velocity跨服&quot;">​</a></h2><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/server [tag]</td><td>切换至FDC下的各个子服（如fdp、ppf），或在其他学校的服务器内切换子服。</td></tr><tr><td><em>/hub</em></td><td>如果你从<a href="/about/server.html">联合大厅</a>进入FDC，可以通过这个指令切换至其他学校的服务器。</td></tr></tbody></table><h2 id="bukkit插件指令" tabindex="-1">Bukkit插件指令 <a class="header-anchor" href="#bukkit插件指令" aria-label="Permalink to &quot;Bukkit插件指令&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>WorldEdit与Axiom的指令不会在此处列出，请前往<a href="/guide/learnspace.html">教程搬运</a>查看相应教程。</p></div><h3 id="消息" tabindex="-1">消息 <a class="header-anchor" href="#消息" aria-label="Permalink to &quot;消息&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/msg</td><td>发送私信给指定玩家。</td></tr><tr><td>/me</td><td>以第三人称描述一件事。</td></tr><tr><td>/r</td><td>快速回复回复你的最后一位玩家。</td></tr><tr><td>/mail</td><td>管理玩家在服务器内的邮件。</td></tr><tr><td>/say</td><td>说话！</td></tr></tbody></table><h3 id="信息" tabindex="-1">信息 <a class="header-anchor" href="#信息" aria-label="Permalink to &quot;信息&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/near</td><td>列出一位玩家附近（或半径内）的玩家。</td></tr><tr><td>/ping</td><td>啪！</td></tr><tr><td>/mspt</td><td>查看服务器当前的MSPT。</td></tr><tr><td>/depth</td><td>指出你当前相对于海平面的位置。</td></tr><tr><td>/rules</td><td>查看服务器的规则。</td></tr><tr><td>/list</td><td>显示所有在线玩家。</td></tr><tr><td>/compass</td><td>描述你当前的方位。</td></tr><tr><td>/getpos</td><td>获取你（或某一玩家）的当前坐标。</td></tr></tbody></table><h3 id="传送" tabindex="-1">传送 <a class="header-anchor" href="#传送" aria-label="Permalink to &quot;传送&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/home &lt;homes&gt;</td><td>传送到家的位置。如果你有多个家，则后面的参数是必需的。</td></tr><tr><td>/sethome &lt;name&gt;</td><td>在当前位置设置家。</td></tr><tr><td>/delhome &lt;name&gt;</td><td>删除一个你创建的家。</td></tr><tr><td>/tpa &lt;player&gt;</td><td>请求传送到指定的玩家。</td></tr><tr><td>/tpahere</td><td>请求指定玩家传送到你的位置。</td></tr><tr><td>/tpaall</td><td>请求所有在线玩家传送到你的位置。</td></tr><tr><td>/tpaccept</td><td>接受传送请求。</td></tr><tr><td>/tpacancel [player]</td><td>取消所有待处理的传送请求。指定[player]来取消他的请求。</td></tr><tr><td>/back</td><td>传送你至先前的位置。</td></tr><tr><td>/spawn</td><td>传送至出生点。</td></tr></tbody></table><h3 id="点歌" tabindex="-1">点歌 <a class="header-anchor" href="#点歌" aria-label="Permalink to &quot;点歌&quot;">​</a></h3><p><strong>注意：需要在客户端上安装与mc版本匹配的<a href="https://www.aliyundrive.com/s/fKHnLh1N5nC/folder/640065ce9bfc693744db43e99b3f8faa737c32d1" target="_blank" rel="noreferrer">AllMusic</a>才能听歌。</strong></p><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/music help</td><td>获取点歌台的指令列表。</td></tr><tr><td>/music &lt;音乐ID&gt;</td><td>使用音乐id点歌，这个方法能准确地点歌，不会点错。</td></tr><tr><td>/music search</td><td>使用歌曲名搜索歌曲，这种方法无需查找id，但是容易点错。</td></tr><tr><td>/music select &lt;序号&gt;</td><td>点相应序号的歌曲。</td></tr><tr><td>/music nextpage</td><td>翻到下一页。</td></tr><tr><td>/music lastpage</td><td>翻到上一页。</td></tr><tr><td>/music vote</td><td>发起投票切歌。如果已经有人发起了投票，输入/music vote来同意。若30秒内，包括发起者在内共有2个人同意切歌，则会切歌，否则仍播放当前歌曲。</td></tr><tr><td>/music list</td><td>查看信息更为详细的歌曲列表。在点歌台的Hud能看到较为粗略的点歌列表。</td></tr><tr><td>/music hud &lt;type&gt; &lt;x&gt; &lt;y&gt;</td><td>设置各种hud的显示位置，默认为左中。type有4个可选项。info：歌曲信息， pic：专辑封面，lyric：歌词，list：播放列表。x与y的值请自行进行调试。</td></tr><tr><td>/music hud enabld &lt;type&gt;</td><td>开启/关闭相应的hud。</td></tr><tr><td>/music hud picsize &lt;size&gt;</td><td>调整专辑封面的大小。</td></tr><tr><td>/music hud reset</td><td>重置<strong>所有</strong>关于hud的设置。</td></tr></tbody></table><h3 id="椅子" tabindex="-1">椅子 <a class="header-anchor" href="#椅子" aria-label="Permalink to &quot;椅子&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/sit</td><td>在当前位置坐下。也可以空手右键台阶或者楼梯来在那个位置坐下。</td></tr><tr><td>/lay</td><td>在当前位置躺下。躺下的方向与你的朝向相同。</td></tr><tr><td>/crawl</td><td>爬！</td></tr><tr><td>/bellyflop</td><td>变成鞘翅飞行的模样，与/crawl有点像。</td></tr><tr><td>/spin</td><td>旋转吧，雪月花！</td></tr></tbody></table><h3 id="旦星一号" tabindex="-1">旦星一号 <a class="header-anchor" href="#旦星一号" aria-label="Permalink to &quot;旦星一号&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/dynmap webregister</td><td>获取旦星一号地空通信的注册码。</td></tr><tr><td>/dmarker</td><td>进行旦星一号的标记点操作，具体见<a href="/guide/sps.html">旦星一号</a>。</td></tr></tbody></table><h3 id="headdatabase" tabindex="-1">HeadDataBase <a class="header-anchor" href="#headdatabase" aria-label="Permalink to &quot;HeadDataBase&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/kit</td><td>获取指定的物品包（或查看所有可用的物品包）。</td></tr><tr><td>/suicide</td><td>自决。</td></tr><tr><td>/afk</td><td>将你标记为暂时离开。</td></tr></tbody></table><h3 id="图片" tabindex="-1">图片 <a class="header-anchor" href="#图片" aria-label="Permalink to &quot;图片&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/images create &lt;url&gt; &lt;scale&gt;</td><td>放置图片，其中url必须为直链，可以将图片上传至免费图床来获得。</td></tr><tr><td>/images delete [near &lt;range&gt;]</td><td>删除图片。不带参数为点击删除图片，带参数为删除一定范围内的图片。再次输入命令以取消。</td></tr></tbody></table><h3 id="其他" tabindex="-1">其他 <a class="header-anchor" href="#其他" aria-label="Permalink to &quot;其他&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>/kit</td><td>获取指定的物品包（或查看所有可用的物品包）。</td></tr><tr><td>/suicide</td><td>自决。</td></tr><tr><td>/afk</td><td>将你标记为暂时离开。</td></tr></tbody></table><h2 id="leaves-假人指令" tabindex="-1">Leaves 假人指令 <a class="header-anchor" href="#leaves-假人指令" aria-label="Permalink to &quot;Leaves 假人指令&quot;">​</a></h2><p>FDC使用的是由服务端Leaves提供的假人系统，假人的总指令为 <code>/bot</code>。它的使用与Carpet的假人系统有着非常大的不同。</p><p><strong>指令列表与说明参见<a href="https://docs.leavesmc.top/zh/leaves/fakeplayer" target="_blank" rel="noreferrer">Leaves Fakeplayer</a>。</strong></p><h2 id="mcdr指令" tabindex="-1">MCDR指令 <a class="header-anchor" href="#mcdr指令" aria-label="Permalink to &quot;MCDR指令&quot;">​</a></h2><p><a href="https://github.com/Fallen-Breath/MCDReforged" target="_blank" rel="noreferrer">MCDR</a>是一个基于python的服务端控制工具，可以给原版端或者其他服务端提供一些插件的支持.由于通过监听服务器内的聊天消息来识别指令，MCDR的指令标识与其他指令不同，一般为<code>!!XXXX</code></p><h3 id="task任务清单" tabindex="-1">Task任务清单 <a class="header-anchor" href="#task任务清单" aria-label="Permalink to &quot;Task任务清单&quot;">​</a></h3><p>非常好用的分锅插件（</p><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>!!task</td><td>显示任务概览。</td></tr><tr><td>!!task help</td><td>获取任务清单插件的指令列表。</td></tr><tr><td>!!task overview</td><td>显示任务概览，同<code>!!task</code>。</td></tr><tr><td>!!task list</td><td>显示完整的任务清单。</td></tr><tr><td>!!task list-all &lt;task&gt;</td><td>显示完整的任务清单（包括子任务）。</td></tr><tr><td>!!task detail &lt;task&gt;</td><td>显示某个任务的详细信息。</td></tr><tr><td>!!task add &lt;task&gt; [description]</td><td>添加一个任务与其描述。</td></tr><tr><td>!!task remove/rm/delete/del &lt;task&gt;</td><td>删除某个任务。</td></tr><tr><td>!!task rename &lt;old task&gt; &lt;new task&gt;</td><td>重命名某个任务。</td></tr><tr><td>!!task change &lt;task&gt; &lt;new description&gt;</td><td>修改某个任务的描述。</td></tr><tr><td>!!task done &lt;task&gt;</td><td>将某个任务标记为完成。</td></tr><tr><td>!!task undone &lt;task&gt;</td><td>将某个任务标记为未完成。</td></tr><tr><td>!!task deadline &lt;task&gt; &lt;period: day&gt;</td><td>设置某个任务的ddl。</td></tr><tr><td>!!task deadline &lt;task&gt; clear</td><td>清除某个任务的ddl。</td></tr><tr><td>!!task priority &lt;task&gt; &lt;priority&gt;</td><td>设置某个任务的优先级。</td></tr><tr><td>!!task player &lt;task&gt;</td><td>查看某个玩家的任务列表。</td></tr><tr><td>!!task res/responsible &lt;task&gt; &lt;player&gt;</td><td>设置某个任务的负责人。</td></tr><tr><td>!!task unres/unresponsible &lt;task&gt; &lt;player&gt;</td><td>删除某个任务的负责人。</td></tr></tbody></table><h3 id="stats数据统计" tabindex="-1">Stats数据统计 <a class="header-anchor" href="#stats数据统计" aria-label="Permalink to &quot;Stats数据统计&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>FDC的建筑榜不是由这个插件构建的，不适用这里的命令。</p></div><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>!!stats</td><td>显示Stats的帮助信息。</td></tr><tr><td>!!stats save &lt;代名&gt; &lt;统计类别&gt; &lt;统计内容&gt; &lt;标题&gt;</td><td>保存一个统计某个统计内容的快速计分板。</td></tr><tr><td>!!stats del &lt;代名&gt;</td><td>移除某个快速计分板。</td></tr><tr><td>!!stats list</td><td>列出已保存的快速计分板。</td></tr><tr><td>!!stats query &lt;玩家&gt; &lt;统计类别&gt; &lt;统计内容&gt; [&lt; -uuid&gt;] [&lt; -tell&gt;]</td><td>查询某个玩家某个统计内容的数值。</td></tr><tr><td>!!stats query &lt;玩家&gt; &lt;代名&gt; [&lt; -uuid&gt;] [&lt; -tell&gt;]</td><td>查询某个玩家某个快速计分板的数值。</td></tr><tr><td>!!stats rank &lt;统计类别&gt; &lt;统计内容&gt; [&lt; -tell&gt;]</td><td>在聊天栏显示某个统计内容的排行。</td></tr><tr><td>!!stats rank &lt;代名&gt; [&lt; -tell&gt;]</td><td>在聊天栏显示某个快速计分板的排行。</td></tr><tr><td>!!stats scoreboard &lt;统计类别&gt; &lt;统计内容&gt; (标题)</td><td>显示一个一个统计某个统计内容的快速计分板，但不保存。</td></tr><tr><td>!!stats scoreboard &lt;代名&gt;</td><td>显示某个快速计分板。</td></tr><tr><td>!!stats scoreboard show</td><td>显示该插件的计分板。</td></tr><tr><td>!!stats scoreboard hide</td><td>隐藏该插件的计分板</td></tr></tbody></table><h4 id="参数说明" tabindex="-1">参数说明 <a class="header-anchor" href="#参数说明" aria-label="Permalink to &quot;参数说明&quot;">​</a></h4><table tabindex="0"><thead><tr><th>参数</th><th>描述</th></tr></thead><tbody><tr><td>&lt;统计类别&gt;</td><td>killed, killed_by, dropped, picked_up, used, mined, broken, crafted, custom</td></tr><tr><td>&lt;统计内容&gt;</td><td>killed, killed_by：生物ID</td></tr><tr><td></td><td>picked_up, used, mined, broken, crafted：物品/方块ID</td></tr><tr><td></td><td>custom：详见统计信息的json文件，或 <a href="https://minecraft.fandom.com/zh/wiki/%E7%BB%9F%E8%AE%A1%E4%BF%A1%E6%81%AF" target="_blank" rel="noreferrer">MC Wiki</a></td></tr><tr><td></td><td>上述内容无需带<code>minecraft:</code>前缀</td></tr><tr><td>[&lt; -uuid&gt;]</td><td>用uuid替换玩家名。</td></tr><tr><td>[&lt; -tell&gt;]</td><td>仅自己可见。</td></tr></tbody></table><h3 id="其他-1" tabindex="-1">其他 <a class="header-anchor" href="#其他-1" aria-label="Permalink to &quot;其他&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>描述</th></tr></thead><tbody><tr><td>!!jrrp</td><td>获取今日人品！</td></tr><tr><td>!!joinMOTD</td><td>显示入服欢迎。</td></tr><tr><td>!!day</td><td>查询开服时间。</td></tr><tr><td>!!hitokoto</td><td>获取一句<a href="https://hitokoto.cn/" target="_blank" rel="noreferrer">一言</a>！</td></tr><tr><td>!!21points</td><td>进行 21 点<s>决斗</s>游戏。</td></tr></tbody></table>',39)]))}const k=d(l,[["render",s]]);export{u as __pageData,k as default};
